import "./style.css"
import CardCarrinho from "./cartcheio";
function CarrinhoCompras({
  currentSale,
  handleRemove,
  setCurrentSale,
  totalPrice,
  resultPrice,
  setCartTotal,
}) {
  const removeAll = () => setCurrentSale([]);

  return (
    <div className="carrinho">
      <h1 className="title">Carrinho de compras</h1>
      {currentSale.map((item) => (
        <CardCarrinho
          currentSale={currentSale}
          totalPrice={totalPrice}
          handleRemove={handleRemove}
          key={item.id}
          item={item}
        />
      ))}
      {currentSale.length < 1 ? (
        <div className="sacolaVazia">
          <h1>Sua sacola est√° vazia</h1> <p>Adicione itens</p>
        </div>
      ) : (
        <div className="carrinho-comProd"></div>
      )}
      {currentSale.length > 0 ? (
        <div className="containerPrice">
          {resultPrice > 0 ? (
            <div>
              <p className="lista"></p>
                <div className="totalPrice">
                  <h1>Total</h1> 
                  <h2>{resultPrice.toLocaleString("pt-BR", {
            style: "currency",
            currency: "BRL",
          })}</h2>
                </div>
            </div>

          ) : (
            ""
          )}
          <button
            className="removeAll"
            onClick={() => {
              removeAll();
              setCartTotal(0);
            }}
          >
            Remover todos
          </button>
        </div>
      ) : (
        ""
      )}
    </div>
  );
}

export default CarrinhoCompras;
